mapper.insert:

insert into trvl_pay_info (

	trvl_id
  , pay_amt
  , pay_dt
  , pay_name
  , pay_method
  , user_id
  , pay_type
  , pay_id
  , input_id
  , input_dt
  , modify_id
  , modify_dt
)
values (
    #{trvlId}
  , #{payAmt}
  , #{payDt}
  , #{payName}
  , #{payMethod}
  , #{userId}
  , #{payType}
  , #{payId}
  , #{userId}
  , now()
  , #{userId}
  , now()
)

mapper.update:
		update trvl_pay_info
		   set pay_amt 		= #{payAmt}
			 , pay_dt 		= #{payDt}
			 , pay_name 	= #{payName}
			 , pay_method 	= #{payMethod}
			 , pay_type		= #{payType}
			 , modify_id 	= #{userId}
			 , modify_dt 	= now()
       where trvl_pay_id = #{trvlPayId}

mapper.select:
select tpi.trvl_id
  from trvl_pay_info tpi
 where tpi.trvl_pay_id = #{trvlPayId}
